var Composition = require('Composition');

module.exports = function(audioPath){

  var BackgroundMusic = new Composition();

  var audio = document.createElement("audio");

  BackgroundMusic.start = function(){
    audio.src = audioPath;
    audio.loop = true;
    audio.play();
  }

  BackgroundMusic.pause = function(){
    audio.pause();
  }

  BackgroundMusic.play = function(){
    audio.play();
  }

  return BackgroundMusic;

}
