var Composition = require('Composition');


var CharacterController = new Composition();

CharacterController.keydown = function(e){
	if(e==39){ //Right arrow is down
		this.base.rigidbody.speed.x = 25;
	}else if(e==37){ //Left arrow is down
		this.base.rigidbody.speed.x = -25;
	}
}

CharacterController.keypress = function(e){
	if(e==32){//Spacebar is pressed
		this.base.rigidbody.speed.y = -50;
	}
}

CharacterController.keyup = function(e){
	if(e==39 || e==37){
		this.base.rigidbody.speed.x = 0;
	}
}

module.exports = CharacterController;