var Composition = require('Composition');
var dataIO = require('dataIO');

module.exports = function(){
  var GameScore = new Composition();

  GameScore.score = 0;
  GameScore.addLife  = function(lifeRemaining){
    this.score += (lifeRemaining*100);
  }
  GameScore.addHit = function(){
    this.score += 10;
  }
  
  return GameScore;
}
