var Composition = require('Composition');

module.exports = function(gravityAcceleration) {
	var Gravity = new Composition();

	Gravity.start = function() {
		this.base.rigidbody.acceleration.y = gravityAcceleration;			
	}

	Gravity.update = function(deltaTime) {
		if(this.base.collider.isGrounded) {
			this.base.rigidbody.acceleration.y = 0;
		} else {
			this.base.rigidbody.acceleration.y = gravityAcceleration;			
		}

	}

	return Gravity;
}