//Create start level
var Composition = require('Composition');
var terrain = require('terrain');
var player = require('player');
var sky = require('sky');
var sun = require('sun');
var testBall = require('testBall');
var BackgroundMusic = require('BackgroundMusic');

module.exports = function(){

  var MainLevel = new Composition(
    'sun', sun(),
    'sky', sky(),
    'terrain', terrain(),
    'player', player(0, 100, "resources/player1.png"),
    'player2', player(800, 100, "resources/player2.png"),
    'testBall', testBall,
    'BackgroundMusic', BackgroundMusic("resources/track.ogg")
  );

  return MainLevel;
}
