//Create start level
var Composition = require('Composition');
var terrain = require('terrain');
var player = require('player');
var sky = require('sky');
var sun = require('sun');
var TurnManager = require('TurnManager');
var SpellBall = require('SpellBall');
var BackgroundMusic = require('BackgroundMusic');
var Pauser = require('Pauser');
var Timer = require('Timer');

module.exports = function(){


	var p1 = player(10, 100, "resources/player1.png");
	var p2 = player(750, 100, "resources/player2.png")
	var MainLevel = new Composition(
	    'sun', sun(),
	    'sky', sky(),
	    'terrain', terrain(),
	    'player', p1,
	    'player2', p2,
	    'turnManager', TurnManager(p1, p2),
	    // 'spellBall', SpellBall(),
		'BackgroundMusic', BackgroundMusic("resources/track.ogg"),
		'Pauser', Pauser(),
		'Timer', Timer()
	);

	return MainLevel;
}
