var Composition = require('Composition');

module.exports = function(){
	var Pauser = new Composition();

	Pauser.keypress = function(key){
		if(key==112){//If key is p key
			this.pause();
		}
	}
	Pauser.pause = function(){
		engine.pause();
		document.getElementById("pause").className = "pauseDiv";
		if(engine.isPaused){
			this.base.BackgroundMusic.pause();
		}else{
			this.base.BackgroundMusic.play();
			document.getElementById("pause").className += " hidden";
		}
	}
	return Pauser
}
