//Rigidbody Composition
//Currently only defines speed and acceleration
var Composition = require('Composition');
var Vec2 = require("vec2");

module.exports = function(){
	var Rigidbody = new Composition();

	Rigidbody.speed = new Vec2(0,0);
	Rigidbody.acceleration = new Vec2(0,0);

	Rigidbody.update = function(timeDelta){
		//Simple euler integration
		//(v = a*t)
		this.speed.x += this.acceleration.x*timeDelta;
		this.speed.y += this.acceleration.y*timeDelta;

		//(p = v*t);
		this.base.transform.localPosition.x += this.speed.x*timeDelta;
		this.base.transform.localPosition.y += this.speed.y*timeDelta;
	}

	return Rigidbody;
};
