/*
Transform Composition
Stores x and y coordinates
Handles parenting
*/
var Composition = require('Composition');
var Vec2 = require('vec2');

module.exports = function(x, y){
	//Create an empty Composition
	var Transform = new Composition();

	//Set local position to x param
	Transform.localPosition = new Vec2(0, 0);
	//Set global position to 0
	Transform.position = new Vec2(0,0);

	//Set parent to none
	Transform.parent = new Vec2(0,0);
	
	//Applies parent transform to this object
	Transform.start = function(){
		this.position.x = this.localPosition.x + this.parent.x;
		this.position.y = this.localPosition.y + this.parent.y;
	}
	
	//Applies parent transform to this object
	Transform.update = function(deltaTime){
		this.position.x = this.localPosition.x + this.parent.x;
		this.position.y = this.localPosition.y + this.parent.y;
	}
	
	//Return creatd Composition
	return Transform;
}