/*
Transform Composition
Stores x and y coordinates
Handles parenting
*/
var Composition = require('Composition');

module.exports = function(x, y){
	//Create an empty Composition
	var Transform = new Composition();

	//Set local X to x param
	Transform.localX = x;
	//Set local Y to y param
	Transform.localY = y;
	//Set global x to 0
	Transform.x = 0;
	//Set global y to 0
	Transform.y = 0;

	//Set parent to none
	Transform.parent = {x:0,y:0};
	
	//Applies parent transform to this object
	Transform.start = function(){
		this.x = this.localX + this.parent.x;
		this.y = this.localY + this.parent.y;
	}
	
	//Applies parent transform to this object
	Transform.update = function(deltaTime){
		this.x = this.localX + this.parent.x;
		this.y = this.localY + this.parent.y;
	}
	
	//Return creatd Composition
	return Transform;
}