var fs = require("fs");

module.exports = {
  //Reader property
  readFile: function(){
    var data = fs.readFileSync('data.json');
    return JSON.parse(data);
  },
  //Writer property
  writeFile: function(dataObj){
    var data = JSON.stringify(dataObj);
    fs.writeFileSync('data.json', data);
  }

}
