var Composition = require('Composition');
var Transform = require('Transform');
var Renderer = require('VertexRenderer');

var head = new Composition(
	'transform', Transform(0,50),
	'renderer', Renderer([{x:-10, y:-10},{x:10, y:-10},{x:10, y:10},{x:-10, y:10}])
);

head.start = function(){
	this.components.transform.parent = this.base.transform;
}

var time = 0;
head.update = function(timeDelta){
	time+=timeDelta;
	this.components.transform.localX = 50*Math.cos(time);
	this.components.transform.localY = 50*Math.sin(time);
}

module.exports = head;