var Composition = require('Composition');
var Transform = require('Transform');
var ImageRenderer = require('ImageRenderer');
var Rigidbody = require('Rigidbody');
var characterCollider = require('CharacterCollider');
var CharacterController = require('CharacterController');
var gravity = require('Gravity');

module.exports = function(x, y, n){
	if(n == 1){
		var	spriteURL = "resources/player1.png";
	}else if(n == 2){
		var	spriteURL = "resources/player2.png";
	}
	var player = new Composition(
		'transform', Transform(x,y),
		'controller', CharacterController(),
		'rigidbody', Rigidbody(),
		'collider', characterCollider(),
		'renderer', ImageRenderer(spriteURL, 1),
		'gravity', gravity(100)
	);

	return player;
}
