 var Vec2 = function(x, y){
	this.x = x;
	this.y = y;

	this.clone = function() {
		return new Vec2(this.x, this.y);
	};

	this.add = function(vec, newInstance){
		if(newInstance){
			var result = this.clone()
			if(vec instanceof Vec2){
				result.x += vec.x;
				result.y += vec.y;
			}
			else{
				result.x += vec;
				result.y += vec
			}
			return result;
		}else{
			if(vec instanceof Vec2){
				this.x += vec.x;
				this.y += vec.y;
			}
			else{
				this.x += vec;
				this.y += vec
			}
		}
	}

	this.subtract = function(vec2, newInstance){
		if(newInstance){
			return new Vec2(this.x-vec2.x, this.y-vec2.y);
		}else{
			this.x -= vec2.x;
			this.y -= vec2.y;
		}
	}
	this.multiply = function(vec, newInstance){
		if(newInstance){
			var result = this.clone
			if(vec instanceof Vec2){
				result.x *= vec.x;
				result.y *= vec.y;
			}else{
				result.x *= vec;
				result.y *= vec;
			}
			return result;
		}else{
			if(vec instanceof Vec2){
				this.x *= vec.x;
				this.y *= vec.y;
			}else{
				this.x *= vec;
				this.y *= vec;
			}

		}
		
	}

	this.normalize = function(r){
		var mag = Math.sqrt(this.x*this.x+this.y*this.y);
		if(r){
			if(mag==0){
				return this.clone();
			}else{
				return new Vec2(this.x/mag, this.y/mag);
			}
		}
		if(mag!=0){
			this.x /= mag;
			this.y /= mag;
		}
	}

	this.magnitude = function(){
		return Math.sqrt(this.x*this.x+this.y*this.y);
	}

	this.zero = function(r){
		if(r){
			return new Vec2(0,0);
		}
		this.x = 0;
		this.y = 0;
	}
}
module.exports = Vec2;