 var Vec2 = function(x, y){
	this.x = x;
	this.y = y;

	this.clone = function() {
		return new Vec2(this.x, this.y);
	};

	this.add = function(vec){
		if(vec instanceof Vec2){
			this.x += vec.x;
			this.y += vec.y;
		}
		else{
			this.x += vec;
			this.y += vec
		}
	}
	this.multiply = function(vec){
		if(vec instanceof Vec2){
			this.x *= vec.x;
			this.y *= vec.y;
		}else{
			this.x *= vec;
			this.y *= vec;
		}
		
	}

	this.normalize = function(r){
		var mag = Math.sqrt(this.x*this.x+this.y*this.y);
		if(r){
			if(mag==0){
				return this.clone();
			}else{
				return new Vec2(this.x/mag, this.y/mag);
			}
		}
		if(mag!=0){
			this.x /= mag;
			this.y /= mag;
		}
	}

	this.zero = function(r){
		if(r){
			return new Vec2(0,0);
		}
		this.x = 0;
		this.y = 0;
	}
}
module.exports = Vec2;